package cat.joanpujol.smasolar.emeter;

import io.netty.buffer.ByteBuf;
import io.netty.buffer.ByteBufUtil;
import io.netty.buffer.Unpooled;

public class EMeterTestUtils {
  private static final String CORRECT_MSG =
      "";
  private static final String INCORRECT_MSG = "11" + CORRECT_MSG;

  public static ByteBuf getCorrectMessage() {
    return Unpooled.wrappedBuffer(ByteBufUtil.decodeHexDump(CORRECT_MSG));
  }

  public static ByteBuf getInCorrectMessage() {
    return Unpooled.wrappedBuffer(ByteBufUtil.decodeHexDump(INCORRECT_MSG));
  }
}
